mapper.insert:
INSERT INTO applicants (
id, application_number, application_status, payment_status,
submission_date, guardian_id, relationship_to_guardian,
previous_school_name, previous_class, application_section, intended_class
) VALUES (
#{id}, #{applicationNumber}, #{applicationStatus}, #{paymentStatus},
#{submissionDate}, #{guardian.id}, #{relationshipToGuardian},
#{previousSchoolName}, #{previousClass}, #{applicationSection}, #{intendedClass}

)

mapper.select:
SELECT a.*, p.first_name, p.middle_name, p.last_name, p.gender, p.date_of_birth, p.created_at, p.updated_at FROM applicants a JOIN persons p ON a.id = p.id WHERE ( LOWER(p.first_name) LIKE LOWER(CONCAT('%', #{query}, '%')) OR LOWER(p.last_name) LIKE LOWER(CONCAT('%', #{query}, '%')) OR LOWER(p.middle_name) LIKE LOWER(CONCAT('%', #{query}, '%')) OR LOWER(a.application_number) LIKE LOWER(CONCAT('%', #{query}, '%')) )
AND a.application_status = #{status}
ORDER BY p.created_at DESC

SELECT a.*, p.first_name, p.middle_name, p.last_name, p.gender, p.date_of_birth, p.created_at, p.updated_at FROM applicants a JOIN persons p ON a.id = p.id
a.application_status = #{status}
ORDER BY p.created_at DESC

mapper.update:
UPDATE applicants
SET application_status = 'APPROVED'
WHERE id = #{id};

UPDATE persons
SET updated_at = NOW()
WHERE id = #{id};

UPDATE applicants
SET application_status = 'REJECTED'
WHERE id = #{id};

UPDATE persons
SET updated_at = NOW()
WHERE id = #{id};

UPDATE applicants
SET application_status = 'PENDING'
WHERE id = #{id};

UPDATE persons
SET updated_at = NOW()
WHERE id = #{id};

