mapper.select:
SELECT * FROM students s
JOIN persons p ON s.id = p.id
WHERE s.guardian_id = #{guardianId}

mapper.insert:
INSERT INTO persons (
first_name, middle_name, last_name, gender, date_of_birth
) VALUES (
#{firstName}, #{middleName}, #{lastName}, #{gender}, #{dateOfBirth}
)

INSERT INTO students (
id, admission_number, admission_date, class_id, school_level,
blood_group, genotype, known_allergies, photo, application_status,
previous_school_name, previous_class, guardian_id
) VALUES (
#{id}, #{admissionNumber}, #{admissionDate}, #{currentClass.id}, #{schoolLevel},
#{bloodGroup}, #{genotype}, #{knownAllergies}, #{photo}, #{applicationStatus},
#{previousSchoolName}, #{previousClass}, #{guardian.id}
)
